package Prog;

public class eleventh {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5;
		int k=1;
		for(int i=1;i<=a;i++)
		{
			for(int j=1;j<=i;j++)
			{
				System.out.print((i+j)%2+" ");
				k++;
				
			}
			//k++;
			System.out.println();
			
			
		}
		for(int i=1;i<=a;i++)
		{
			for(int j=1;j<=i;j++)
			{
				System.out.print((i+j+1)%2+" ");
				k++;
				
			}
			//k++;
			System.out.println();
			
		

	}

	}
}
